package plus_example;

public class Pg33 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int hap = 0;
		for(int b =1; b<=10; b++) {
			hap += b;
		}
		System.out.printf("1~10까지의 합 -> %d \n", hap);
		for(int b=1; b<=20; b++) {
			hap += b;
		}
		System.out.printf("1~20까지의 합 -> %d \n", hap);
		for(int b=1; b<=30; b++) {
			hap += b;
		}
		System.out.printf("1~30까지의 합 -> %d \n", hap);
		for(int b=1; b<=40; b++) {
			hap += b;
		}
		System.out.printf("1~40까지의 합 -> %d \n", hap);
		for(int b=1; b<=50; b++) {
			hap += b;
		}
		System.out.printf("1~50까지의 합 -> %d \n", hap);
		
	}

}
